import{_ as e,c as k,a5 as a,G as r,B as o,o as n}from"./chunks/framework.O9ad3XaM.js";const m=JSON.parse('{"title":"NukkitX 核心介绍","description":"","frontmatter":{"title":"NukkitX 核心介绍","sidebar_position":1},"headers":[],"relativePath":"Bedrock/nukkit-core/start/nukkitx-core/nukkitx/nukkitx.md","filePath":"nitwikit/docs-bedrock/nukkit-core/start/nukkitx-core/nukkitx/nukkitx.md","lastUpdated":1749617524000}'),u={name:"Bedrock/nukkit-core/start/nukkitx-core/nukkitx/nukkitx.md"};function s(l,t,c,d,p,h){const i=o("nw-image-viewer");return n(),k("div",null,[t[0]||(t[0]=a('<p><img src="https://www.minebbs.com/attachments/banner-png.21695/" alt="NukkitXLogo"></p><h2 id="什么是-nukkitx" tabindex="-1">什么是 NukkitX <a class="header-anchor" href="#什么是-nukkitx" aria-label="Permalink to &quot;什么是 NukkitX&quot;">​</a></h2><p>NukkitX（现通称 Nukkit）是由 CloudburstMC 团队维护的 Nukkit 分支版本，是被原 Nukkit 团队官方认可的继承者。 NukkitX 的 GitHub 仓库首次提交时间不详，但其作为 Nukkit 的继承者，继承了 Nukkit 的特性，并继续进行维护和更新。</p><p>NukkitX 的开发团队 CloudburstMC 专注于维护和改进 NukkitX，但不涉及对新特性的开发。</p><h2 id="特色" tabindex="-1">特色 <a class="header-anchor" href="#特色" aria-label="Permalink to &quot;特色&quot;">​</a></h2><ul><li><strong>高性能与稳定性</strong>：NukkitX 使用 Java 编写，运行速度更快且更加稳定。</li><li><strong>良好的插件生态</strong>：NukkitX 拥有友好的结构，开发者可以轻松参与开发，并将其他平台的插件移植到 NukkitX。</li><li><strong>多版本支持</strong>：NukkitX 几乎支持所有 Minecraft 基岩版的版本。</li><li><strong>持续更新</strong>：CloudburstMC 团队积极维护 NukkitX，不断更新以支持新的游戏版本和修复问题。</li></ul><h2 id="链接" tabindex="-1">链接 <a class="header-anchor" href="#链接" aria-label="Permalink to &quot;链接&quot;">​</a></h2><ul><li><a href="https://github.com/CloudburstMC/Nukkit" target="_blank" rel="noreferrer">Github 链接</a></li></ul>',8)),r(i)])}const b=e(u,[["render",s]]);export{m as __pageData,b as default};
