import{_ as a,c as i,a5 as r,G as o,B as n,o as l}from"./chunks/framework.O9ad3XaM.js";const N=JSON.parse('{"title":"PowerNukkitX 核心介绍","description":"","frontmatter":{"title":"PowerNukkitX 核心介绍","sidebar_position":1},"headers":[],"relativePath":"Bedrock/nukkit-core/start/pnx-core/pnx/pnx.md","filePath":"nitwikit/docs-bedrock/nukkit-core/start/pnx-core/pnx/pnx.md","lastUpdated":1749617524000}'),k={name:"Bedrock/nukkit-core/start/pnx-core/pnx/pnx.md"};function c(s,e,p,u,d,h){const t=n("nw-image-viewer");return l(),i("div",null,[e[0]||(e[0]=r('<p><img src="https://www.minebbs.com/attachments/pnx_banner-png.25436/" alt="PowerNukkitXLogo"></p><h2 id="什么是-powernukkitx" tabindex="-1">什么是 PowerNukkitX <a class="header-anchor" href="#什么是-powernukkitx" aria-label="Permalink to &quot;什么是 PowerNukkitX&quot;">​</a></h2><p>PowerNukkitX（简称 PNX）是由包括 BlocklyNukkit 团队在内的中国 Nukkit 开发者们负责开发维护的 Nukkit 分支版本， 修复了众多 BUG，改善了性能，及时跟进新版本支持、支持 1.19 所有新方块和新物品、支持新版下界、支持史诗地形生成器 (Terra)。 在这些大更改的情况下，PNX 仍然保持了良好的插件兼容性，Nukkit 生态中的数千个插件仍然可以在 PNX 上运行。</p><p>得益于完全开源的代码，您在遵守开源协议的情况下使用 PowerNukkitX 没有任何法律风险， 内部的数十万 API 可以随意调用，您可以深入查看实现， 通过 JVM 生态中的各种工具及其方便地进行开发、调试、注入、拦截。也正因如此， Nukkit 生态丰富多彩，成百上千各种各样的插件供您选择，您可以用您喜欢的任意 JVM 语言编写插件，如 Java,Kotlin,Scala…… ，同时也支持使用 Javascript 编写插件。</p><h2 id="特色" tabindex="-1">特色 <a class="header-anchor" href="#特色" aria-label="Permalink to &quot;特色&quot;">​</a></h2><ul><li>支持基岩版 1.19.50 协议。</li><li>原生支持 384 限高（仅主世界）。</li><li>原生支持地狱世界，无需另外安装插件补丁等。</li><li>支持 3D 生物群系（完善中）。</li><li>原生支持原版命令和命令方块等（完善中）。</li><li>内置 Terra 地形生成器（如有问题点此查看）。</li><li>支持使用 JavaSrcipt 语言编写插件（初步完成，开发文档在此处）。</li><li>支持自定义方块 / 物品 / 实体（完善中，文档待补充）。</li><li>内置生物 AI，无需安装 MobPlugin（开发中，未完善）。</li></ul><h2 id="链接" tabindex="-1">链接 <a class="header-anchor" href="#链接" aria-label="Permalink to &quot;链接&quot;">​</a></h2><ul><li><a href="https://github.com/PowerNukkitX/PowerNukkitX" target="_blank" rel="noreferrer">Github 链接</a></li></ul>',8)),o(t)])}const w=a(k,[["render",c]]);export{N as __pageData,w as default};
