import{_ as r,c as t,a5 as a,o as i}from"./chunks/framework.O9ad3XaM.js";const p=JSON.parse('{"title":"MCPEServer 核心介绍","description":"","frontmatter":{"title":"MCPEServer 核心介绍","sidebar_position":1},"headers":[],"relativePath":"Bedrock/qitabe-core/mcpeserver/mcpeserver.md","filePath":"nitwikit/docs-bedrock/qitabe-core/mcpeserver/mcpeserver.md","lastUpdated":1749617524000}'),o={name:"Bedrock/qitabe-core/mcpeserver/mcpeserver.md"};function s(n,e,l,c,d,h){return i(),t("div",null,e[0]||(e[0]=[a('<h1 id="什么是-mcpeserver" tabindex="-1">什么是 MCPEServer <a class="header-anchor" href="#什么是-mcpeserver" aria-label="Permalink to &quot;什么是 MCPEServer&quot;">​</a></h1><p>mcpeserver 是一个用 Golang 编写的 Minecraft 服务器启动器。 它利用 Minecraft 安卓 APK 文件中的内置服务器组件来运行一个原生的基岩版（Bedrock）服务器。 该项目已经标记为“已弃用”，建议使用官方的 BDS（Bedrock Dedicated Server）。</p><h2 id="支持版本" tabindex="-1">支持版本 <a class="header-anchor" href="#支持版本" aria-label="Permalink to &quot;支持版本&quot;">​</a></h2><ul><li><strong>服务器核心版本</strong>：当前发布的版本支持 Minecraft 1.6.0 作为服务器核心。</li><li><strong>客户端版本</strong>：所有 1.6.x 版本的客户端都可以连接到该服务器。</li></ul><h2 id="特点" tabindex="-1">特点 <a class="header-anchor" href="#特点" aria-label="Permalink to &quot;特点&quot;">​</a></h2><ul><li><strong>自动补全命令</strong>：支持命令行自动补全功能。</li><li><strong>完整的基岩版服务器功能</strong>：支持 Minecraft 基岩版的所有功能和特性。</li><li><strong>基于 Systemd 的服务</strong>：可以作为 Systemd 服务运行，便于管理。</li><li><strong>基于 DBus 的接口</strong>：支持 DBus 接口，便于与其他系统组件交互。</li><li><strong>配置灵活</strong>：提供详细的服务器配置文件，用户可以自定义服务器的各种设置，例如世界名称、端口、最大玩家数等。</li><li><strong>易于安装和管理</strong>：提供了针对 Arch Linux 的安装指南，其他 Linux 发行版也可以参考相关文档进行安装。</li><li><strong>支持 Xbox Live 成就</strong>：玩家可以在服务器上获得 Xbox Live 成就，就像在 Minecraft Realms 中一样。</li></ul><h2 id="链接" tabindex="-1">链接 <a class="header-anchor" href="#链接" aria-label="Permalink to &quot;链接&quot;">​</a></h2><ul><li><a href="https://github.com/codehz/mcpeserver" target="_blank" rel="noreferrer">Github 链接</a></li></ul>',8)]))}const u=r(o,[["render",s]]);export{p as __pageData,u as default};
