import{_ as t,c as r,a5 as a,o as l}from"./chunks/framework.O9ad3XaM.js";const c=JSON.parse('{"title":"GeyserMEG4支持","description":"","frontmatter":{"title":"GeyserMEG4支持","sidebar_position":6},"headers":[],"relativePath":"Java/process/mobile-player/Geyser/Upgrade/GeyserMEG4-supports.md","filePath":"nitwikit/docs-java/process/mobile-player/Geyser/Upgrade/GeyserMEG4-supports.md","lastUpdated":1737795064000}'),s={name:"Java/process/mobile-player/Geyser/Upgrade/GeyserMEG4-supports.md"};function n(i,e,o,d,g,y){return l(),r("div",null,e[0]||(e[0]=[a('<h1 id="geysermeg4支持" tabindex="-1">GeyserMEG4支持 <a class="header-anchor" href="#geysermeg4支持" aria-label="Permalink to &quot;GeyserMEG4支持&quot;">​</a></h1><h2 id="geysermodelengine" tabindex="-1">GeyserModelEngine <a class="header-anchor" href="#geysermodelengine" aria-label="Permalink to &quot;GeyserModelEngine&quot;">​</a></h2><p>一个能让你Geyser服务器支持MEG4的奇妙插件，通过给基岩版发送个史蒂夫发送4d皮肤给基岩版玩家，支持原版Geyser</p><p>只需要往资源包里塞动画文件即可，可以有效防止那些进服下个资源包就跑白嫖你模型的贼，但同时限制也挺多</p><h3 id="前置" tabindex="-1">前置 <a class="header-anchor" href="#前置" aria-label="Permalink to &quot;前置&quot;">​</a></h3><table tabindex="0"><thead><tr><th style="text-align:left;">前置插件</th><th style="text-align:left;">橡木链接</th><th style="text-align:left;">作用</th></tr></thead><tbody><tr><td style="text-align:left;">GeyserUtils</td><td style="text-align:left;"><a href="https://github.com/zimzaza4/GeyserUtils" target="_blank" rel="noreferrer">GeyserUtils</a></td><td style="text-align:left;">让你的Geyser支持调用一些BE的东西</td></tr><tr><td style="text-align:left;">GeyserModelEngine</td><td style="text-align:left;"><a href="https://github.com/zimzaza4/GeyserModelEngine" target="_blank" rel="noreferrer">GitHub</a></td><td style="text-align:left;">你猜</td></tr><tr><td style="text-align:left;">LibsDisguises(免费版即可)</td><td style="text-align:left;"><a href="https://www.spigotmc.org/resources/libs-disguises-free.81/" target="_blank" rel="noreferrer">LibsDisguises</a></td><td style="text-align:left;">伪装成其他生物</td></tr></tbody></table><p>项目地址有简体中文，所以后续步骤我就不写了</p><p><a href="https://github.com/zimzaza4/GeyserModelEngine" target="_blank" rel="noreferrer">项目地址</a></p><h2 id="geysermodelengineblockbenchpacker" tabindex="-1">GeyserModelEngineBlockbenchPacker <a class="header-anchor" href="#geysermodelengineblockbenchpacker" aria-label="Permalink to &quot;GeyserModelEngineBlockbenchPacker&quot;">​</a></h2><p><a href="https://github.com/GeyserExtensionists/GeyserModelEngineBlockbenchPacker" target="_blank" rel="noreferrer">GitHub</a></p><p>一个 Blockbench 插件,可以帮助你将 bbmodel 导出为 GeyserModelEngine 的模型格式</p><h2 id="geysermodelengine-自定义实体分支" tabindex="-1">GeyserModelEngine (自定义实体分支) <a class="header-anchor" href="#geysermodelengine-自定义实体分支" aria-label="Permalink to &quot;GeyserModelEngine (自定义实体分支)&quot;">​</a></h2><div class="warning custom-block"><p class="custom-block-title">警告</p><p>这个是真自定义实体! 不支持原版Geyser，你需要搭配<a href="https://github.com/zimzaza4/Geyser" target="_blank" rel="noreferrer">Geyser 自定义实体分支</a>和资源包一起使用才能正常工作</p><p>你可以使用<code>GeyserModelEnginePackGenerator</code>来自动生成资源包，详细教程看项目地址</p></div><h3 id="前置-1" tabindex="-1">前置 <a class="header-anchor" href="#前置-1" aria-label="Permalink to &quot;前置&quot;">​</a></h3><table tabindex="0"><thead><tr><th style="text-align:left;">前置插件</th><th style="text-align:left;">项目链接</th><th style="text-align:left;">作用</th></tr></thead><tbody><tr><td style="text-align:left;">GeyserUtils</td><td style="text-align:left;"><a href="https://github.com/zimzaza4/GeyserUtils" target="_blank" rel="noreferrer">GitHub</a></td><td style="text-align:left;">让你的Geyser支持调用一些BE的东西</td></tr><tr><td style="text-align:left;">GeyserModelEngine自定义实体分支</td><td style="text-align:left;"><a href="https://github.com/zimzaza4/GeyserModelEngine/tree/custom-entity" target="_blank" rel="noreferrer">GitHub</a></td><td style="text-align:left;">你猜</td></tr><tr><td style="text-align:left;">Geyser 自定义实体分支</td><td style="text-align:left;"><a href="https://github.com/zimzaza4/Geyser" target="_blank" rel="noreferrer">GitHub</a></td><td style="text-align:left;">让Geyser支持自定义实体</td></tr><tr><td style="text-align:left;">GeyserModelEnginePackGenerator</td><td style="text-align:left;"><a href="https://github.com/zimzaza4/GeyserModelEnginePackGenerator" target="_blank" rel="noreferrer">GitHub</a></td><td style="text-align:left;">帮你自动转换模型生成资源包</td></tr></tbody></table><p>后续更多步骤请看项目地址</p><p><a href="https://github.com/zimzaza4/GeyserModelEngine/tree/custom-entity" target="_blank" rel="noreferrer">项目地址</a></p>',17)]))}const b=t(s,[["render",n]]);export{c as __pageData,b as default};
