import{_ as a,c as e,a5 as r,o}from"./chunks/framework.O9ad3XaM.js";const h=JSON.parse('{"title":"Vault","description":"","frontmatter":{"title":"Vault","slug":"Vault","sidebar_position":2},"headers":[],"relativePath":"Java/process/plugin/Front-Plugin/Vault/vault.md","filePath":"nitwikit/docs-java/process/plugin/Front-Plugin/Vault/vault.md","lastUpdated":1734140411000}'),l={name:"Java/process/plugin/Front-Plugin/Vault/vault.md"};function u(s,t,p,i,n,c){return o(),e("div",null,t[0]||(t[0]=[r('<h1 id="vault" tabindex="-1">Vault <a class="header-anchor" href="#vault" aria-label="Permalink to &quot;Vault&quot;">​</a></h1><div class="info custom-block"><p class="custom-block-title">INFO</p><p><code>Bukkit</code> <a href="https://dev.bukkit.org/projects/vault" target="_blank" rel="noreferrer">https://dev.bukkit.org/projects/vault</a></p><p><code>SpigotMC</code> <a href="https://www.spigotmc.org/resources/.34315" target="_blank" rel="noreferrer">https://www.spigotmc.org/resources/.34315</a></p><p><code>MineBBS</code> <a href="https://www.minebbs.com/resources/.7869" target="_blank" rel="noreferrer">https://www.minebbs.com/resources/.7869</a></p><p><code>GitHub</code> <a href="https://github.com/milkbowl/Vault" target="_blank" rel="noreferrer">https://github.com/milkbowl/Vault</a></p><p><code>插件百科</code> <a href="https://mineplugin.org/Vault" target="_blank" rel="noreferrer">https://mineplugin.org/Vault</a></p></div><h2 id="什么是vault" tabindex="-1">什么是Vault <a class="header-anchor" href="#什么是vault" aria-label="Permalink to &quot;什么是Vault&quot;">​</a></h2><blockquote><p>Vault 是一个 Bukkit 抽象库，为其他插件提供与权限、聊天和经济有关的接口，为插件们提供一套系统性的支持，对于以 Vault 作为前置的插件来说，它是各个插件之间的桥梁、合约。</p></blockquote><p>人话：插件开发者要考虑的，你只管装上去就行了</p><h3 id="vualt是不是经济插件" tabindex="-1">Vualt是不是经济插件 <a class="header-anchor" href="#vualt是不是经济插件" aria-label="Permalink to &quot;Vualt是不是经济插件&quot;">​</a></h3><p>不是。</p><blockquote><p>作为被依赖的前置，它可以为经济插件创建和管理虚拟经济系统，使其他插件能够提供 Vault 与各种经济插件交互，从而使它们能够共享相同的经济系统，其本身并不包含经济插件功能。</p></blockquote><p>人话：Vault只是提供经济的插件的前置，本身并不提供经济功能，就是给众多经济系统提供了一个统一接口</p><p>想要经济可以看看 <a href="./XConomy">XConomy页面</a></p>',10)]))}const b=a(l,[["render",u]]);export{h as __pageData,b as default};
