import{_ as a,c as i,a5 as l,o as r}from"./chunks/framework.O9ad3XaM.js";const u=JSON.parse('{"title":"InvSee++","description":"","frontmatter":{"sidebar_position":4,"sidebar_label":"查包 - InvSee++"},"headers":[],"relativePath":"Java/process/plugin/ManageTool/AntiGrief/InvSee++.md","filePath":"nitwikit/docs-java/process/plugin/ManageTool/AntiGrief/InvSee++.md","lastUpdated":1727754229000}'),n={name:"Java/process/plugin/ManageTool/AntiGrief/InvSee++.md"};function t(o,e,s,c,d,h){return r(),i("div",null,e[0]||(e[0]=[l('<h1 id="invsee" tabindex="-1">InvSee++ <a class="header-anchor" href="#invsee" aria-label="Permalink to &quot;InvSee++&quot;">​</a></h1><div class="info custom-block"><p class="custom-block-title">信息</p><p><code>SpigotMC</code> <a href="https://www.spigotmc.org/resources/invsee.82342/" target="_blank" rel="noreferrer">https://www.spigotmc.org/resources/invsee.82342/</a></p><p><code>Modrinth</code> <a href="https://modrinth.com/plugin/invsee++" target="_blank" rel="noreferrer">https://modrinth.com/plugin/invsee++</a></p><p><code>GitHub</code> <a href="https://github.com/Jannyboy11/InvSee-plus-plus" target="_blank" rel="noreferrer">https://github.com/Jannyboy11/InvSee-plus-plus</a></p></div><p>InvSee++ 可以查看和编辑其他玩家的物品栏和末影箱。</p><p><a href="/Java/process/plugin/ManageTool/BasicPlugins/EssentialsX/">Essentials</a> 也有这个功能，但 InvSee 可以：</p><ul><li>拿走或替换玩家的装备</li><li>查询离线玩家的背包 ...</li></ul><h2 id="功能" tabindex="-1">功能 <a class="header-anchor" href="#功能" aria-label="Permalink to &quot;功能&quot;">​</a></h2><ul><li>查看和编辑其他玩家的物品栏和末影宝箱。</li><li>包括玩家的盔甲内容和副手物品。</li><li>包括玩家的合成/工作台/铁砧/锻造台/石匠/磨石/织布机/附魔台/商人物品以及玩家光标中的物品。</li><li>支持离线玩家，甚至支持从未登录过服务器的玩家。</li><li>当玩家重新登录时，物品栏会自动重新加载。</li><li>(可选)与 PerWorldInventory 集成。这需要在其配置中加入 <code>load-data-on-join： true</code> 。</li></ul><h2 id="命令" tabindex="-1">命令 <a class="header-anchor" href="#命令" aria-label="Permalink to &quot;命令&quot;">​</a></h2><ul><li>/invsee [用户名]/[UUID] (查看背包)</li><li>/endersee [用户名]/[UUID] (看末影箱)</li></ul><h2 id="扩展插件" tabindex="-1">扩展插件 <a class="header-anchor" href="#扩展插件" aria-label="Permalink to &quot;扩展插件&quot;">​</a></h2><h3 id="invsee-give" tabindex="-1">InvSee++ Give <a class="header-anchor" href="#invsee-give" aria-label="Permalink to &quot;InvSee++ Give&quot;">​</a></h3><p>将物品添加到离线玩家的背包/末影箱。</p><h4 id="指令" tabindex="-1">指令 <a class="header-anchor" href="#指令" aria-label="Permalink to &quot;指令&quot;">​</a></h4><ul><li>/invgive (添加到背包)</li><li>/endergive (添加到末影箱)</li></ul><p>指令格式和普通的 give 指令一模一样。</p><h3 id="invsee-clear" tabindex="-1">InvSee++ Clear <a class="header-anchor" href="#invsee-clear" aria-label="Permalink to &quot;InvSee++ Clear&quot;">​</a></h3><p>清除离线玩家背包/末影箱中的物品。</p><h4 id="指令-1" tabindex="-1">指令 <a class="header-anchor" href="#指令-1" aria-label="Permalink to &quot;指令&quot;">​</a></h4><ul><li>/invclear (清除背包中的物品)</li><li>/enderclear (清除末影箱中的物品)</li></ul><p>指令格式和普通的 clear 指令一模一样。</p>',20)]))}const v=a(n,[["render",t]]);export{u as __pageData,v as default};
