import{_ as t}from"./chunks/日志-1.dFsnIEQn.js";import{_ as s,c as i,a5 as o,G as p,B as n,o as l}from"./chunks/framework.O9ad3XaM.js";const c="/assets/editor.63wdOpLJ.png",d="/assets/%E6%97%A5%E5%BF%97-2.n_2Lje6j.png",r="/assets/on.CXslgwcR.png",h="/assets/paste-1.B4l3Oamh.png",m="/assets/paste-2.R7yFZmOP.png",u="/assets/command.BqBEt8G0.png",y=JSON.parse('{"title":"小妙招","description":"","frontmatter":{"title":"小妙招","sidebar_position":5},"headers":[],"relativePath":"Java/process/plugin/ManageTool/Permission/Tips.md","filePath":"nitwikit/docs-java/process/plugin/ManageTool/Permission/Tips.md","lastUpdated":1737953695000}'),g={name:"Java/process/plugin/ManageTool/Permission/Tips.md"};function b(v,a,k,_,x,f){const e=n("nw-image-viewer");return l(),i("div",null,[a[0]||(a[0]=o('<h1 id="小妙招" tabindex="-1">小妙招 <a class="header-anchor" href="#小妙招" aria-label="Permalink to &quot;小妙招&quot;">​</a></h1><h2 id="网页版可视化编辑器" tabindex="-1">网页版可视化编辑器 <a class="header-anchor" href="#网页版可视化编辑器" aria-label="Permalink to &quot;网页版可视化编辑器&quot;">​</a></h2><div class="language-text vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">text</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>lp editor</span></span></code></pre></div><p><img src="'+c+'" alt=""></p><h2 id="日志" tabindex="-1">日志 <a class="header-anchor" href="#日志" aria-label="Permalink to &quot;日志&quot;">​</a></h2><p>可以实时打印权限检查情况，方便排查问题。</p><div class="language-text vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">text</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>lp verbose &lt;on|record|off|upload&gt; [filter]</span></span></code></pre></div><p>例如：</p><p>当安装 EssentialsXAntiBuild 后，你没有权限破坏方块时。</p><p>你还不知道翻文档的情况，也不用跟个傻逼一样问群友这个</p><p><img src="'+t+'" alt=""></p><p>打开 Luckperms 的详细日志：</p><div class="language-text vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">text</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>/lp verbose on</span></span></code></pre></div><p>再次破坏方块时，你会看到：</p><p><img src="'+d+'" alt=""></p><h3 id="解释" tabindex="-1">解释 <a class="header-anchor" href="#解释" aria-label="Permalink to &quot;解释&quot;">​</a></h3><ul><li><code>on</code> - 开启权限检查。</li><li><code>off</code> - 关闭权限检查。</li><li><code>record</code> - 记录日志。</li><li><code>paste</code> - 停止记录，显示日志。</li></ul><h3 id="on" tabindex="-1">on <a class="header-anchor" href="#on" aria-label="Permalink to &quot;on&quot;">​</a></h3><p><img src="'+r+'" alt=""></p><h3 id="过滤器" tabindex="-1">过滤器 <a class="header-anchor" href="#过滤器" aria-label="Permalink to &quot;过滤器&quot;">​</a></h3><div class="language-text vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">text</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>lp verbose on !Luck &amp; !anticheat</span></span></code></pre></div><ul><li><code>Luck &amp; (essentials | worldedit)</code> - 匹配 &#39;essentials&#39;和&#39;worldedit&#39; 开头的权限检查。</li><li><code>!Luck &amp; !anticheat</code> - 匹配除了 &#39;Luck&#39; 和 &#39;anticheat&#39; 开头以外的权限检查。</li><li><code>anticheat &amp; !anticheat.check</code> - 匹配以&#39;anticheat&#39; 开头 但不匹配 &#39;anticheat.check&#39; 开头的权限检查。</li></ul><h3 id="paste" tabindex="-1">paste <a class="header-anchor" href="#paste" aria-label="Permalink to &quot;paste&quot;">​</a></h3><p><img src="'+h+'" alt=""></p><p><img src="'+m+'" alt=""></p><h2 id="执行命令" tabindex="-1">执行命令 <a class="header-anchor" href="#执行命令" aria-label="Permalink to &quot;执行命令&quot;">​</a></h2><p>如果你只是想知道执行一条命令所检查的权限节点，那么没必要使用 <code>/lp verbose on</code></p><p><code>/lp verbose command</code> 让玩家执行命令，并打印此命令的权限检查。</p><div class="language-text vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">text</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>/lp verbose command &lt;me|player&gt; &lt;command&gt;</span></span></code></pre></div><p>例如，在下方截图中让我（“me”）执行 “sf guide” 命令并打印权限检查。</p><p>截图中的 me 可以替换为在线玩家的玩家名。</p><p><img src="'+u+'" alt=""></p>',32)),p(e)])}const C=s(g,[["render",b]]);export{y as __pageData,C as default};
