import{_ as a,c as t,a5 as o,G as l,B as d,o as i}from"./chunks/framework.O9ad3XaM.js";const s="/assets/worldguard-logo.CusorNyi.svg",f=JSON.parse('{"title":"WorldGuard","description":"","frontmatter":{"sidebar_position":1,"sidebar_label":"保护 - WorldGuard"},"headers":[],"relativePath":"Java/process/plugin/WorldManagement/WorldGuard.md","filePath":"nitwikit/docs-java/process/plugin/WorldManagement/WorldGuard.md","lastUpdated":1749617524000}'),n={name:"Java/process/plugin/WorldManagement/WorldGuard.md"};function c(p,r,u,h,g,b){const e=d("nw-image-viewer");return i(),t("div",null,[r[0]||(r[0]=o('<h1 id="worldguard" tabindex="-1">WorldGuard <a class="header-anchor" href="#worldguard" aria-label="Permalink to &quot;WorldGuard&quot;">​</a></h1><p><img src="'+s+'" alt=""></p><p align="center">保护你的服务器！</p><p align="center">WorldGuard 可以阻止玩家放置和破坏方块，同时还可以调整和禁用 Minecraft 的各种游戏机制。</p><h2 id="功能" tabindex="-1">功能 <a class="header-anchor" href="#功能" aria-label="Permalink to &quot;功能&quot;">​</a></h2><p>WorldGuard 可以保护你的区域或世界，使他们免受破坏，或者调整其他选项如：</p><ul><li>阻止潜行者（Creeper）和凋零（Wither）方块造成的伤害、坠落伤害等。</li><li>禁用火焰蔓延、岩浆火焰蔓延、冰形成、末影人（Enderman）捡起方块等功能。</li><li>将某些物品和方块列入黑名单，使其无法使用。</li><li>当使用某些物品和方块时，向管理员发出警告。</li><li>保护您世界中的特定区域，使其只有特定的人才能在其中建造。</li><li>设置区域，在这些区域中禁用玩家 PVP、TNT、怪物伤害以及其他功能。</li><li>保护您的服务器免受各种“漏洞”的影响，如 magical obsidian creation machines。</li><li>禁用或启用 Minecraft 的各种功能，如经典的海绵。</li><li>添加有用的命令，如立即“停止所有火焰蔓延”的命令。</li><li>只启用你想要的功能！默认情况下，所有功能都是关闭的。</li></ul><h2 id="链接" tabindex="-1">链接 <a class="header-anchor" href="#链接" aria-label="Permalink to &quot;链接&quot;">​</a></h2><div class="info custom-block"><p class="custom-block-title">信息</p><p><code>Bukkit</code> <a href="https://dev.bukkit.org/projects/worldguard" target="_blank" rel="noreferrer">https://dev.bukkit.org/projects/worldguard</a></p><p><code>Modrinth</code> <a href="https://modrinth.com/plugin/worldguard" target="_blank" rel="noreferrer">https://modrinth.com/plugin/worldguard</a></p><p><code>MineBBS</code> <a href="https://www.minebbs.com/resources/.9074/" target="_blank" rel="noreferrer">https://www.minebbs.com/resources/.9074/</a></p><p><code>GitHub</code> <a href="https://github.com/EngineHub/WorldGuard" target="_blank" rel="noreferrer">https://github.com/EngineHub/WorldGuard</a></p><p><code>文档(英文)</code> <a href="https://worldguard.enginehub.org/en/latest/index.html" target="_blank" rel="noreferrer">https://worldguard.enginehub.org/en/latest/index.html</a></p><p><code>文档(中文)</code> <a href="https://snowcutieowo.github.io/WorldGuard" target="_blank" rel="noreferrer">https://snowcutieowo.github.io/WorldGuard</a></p><p><code>插件百科</code> <a href="https://mineplugin.org/Worldguard" target="_blank" rel="noreferrer">https://mineplugin.org/Worldguard</a></p></div><h2 id="扩展" tabindex="-1">扩展 <a class="header-anchor" href="#扩展" aria-label="Permalink to &quot;扩展&quot;">​</a></h2><p>额外 flags: <a href="https://www.spigotmc.org/resources/worldguard-extra-flags.4823/" target="_blank" rel="noreferrer">https://www.spigotmc.org/resources/worldguard-extra-flags.4823/</a></p><h2 id="前置" tabindex="-1">前置 <a class="header-anchor" href="#前置" aria-label="Permalink to &quot;前置&quot;">​</a></h2><p><a href="./WorldEdit">WorldEdit</a> 或 <a href="./FastAsyncWorldEdit">FastAsyncWorldEdit</a></p><h2 id="bstats" tabindex="-1">Bstats <a class="header-anchor" href="#bstats" aria-label="Permalink to &quot;Bstats&quot;">​</a></h2><p><a href="https://bstats.org/plugin/bukkit/WorldGuard/3283" target="_blank" rel="noreferrer"><img src="https://bstats.org/signatures/bukkit/WorldGuard.svg" alt=""></a></p>',15)),l(e)])}const w=a(n,[["render",c]]);export{f as __pageData,w as default};
