import{_ as t,c as r,a5 as i,G as o,B as s,o as l}from"./chunks/framework.O9ad3XaM.js";const p="/assets/148869708-310e7ec4-7d89-40e8-8fc6-38d2e6116cb7.DmOY74At.png",c="/assets/155235807-250932d3-0ffd-43ca-92c8-3112df12a64e.CEBaoc3z.png",n="/assets/327982758-d0903a79-e55d-4634-babb-063af2ef0c7c.B4b35gjm.gif",h="/assets/videoframe_0.BP0jVDcp.png",m="/assets/videoframe_5048.DpbgGBA9.png",d="/assets/9d106a9197f3a7b34e5941716f48a3bb0acf76ce.Ue-bliFy.gif",v=JSON.parse('{"title":"杂谈","description":"","frontmatter":{"title":"杂谈","sidebar_position":9},"headers":[],"relativePath":"Java/process/plugin/more/tittle-tattle.md","filePath":"nitwikit/docs-java/process/plugin/more/tittle-tattle.md","lastUpdated":1738042730000}'),f={name:"Java/process/plugin/more/tittle-tattle.md"};function g(b,a,u,_,k,B){const e=s("nw-image-viewer");return l(),r("div",null,[a[0]||(a[0]=i('<h1 id="杂谈" tabindex="-1">杂谈 <a class="header-anchor" href="#杂谈" aria-label="Permalink to &quot;杂谈&quot;">​</a></h1><h2 id="付费-免费-开源" tabindex="-1">付费&gt;免费/开源？ <a class="header-anchor" href="#付费-免费-开源" aria-label="Permalink to &quot;付费&gt;免费/开源？&quot;">​</a></h2><p>并非如此！</p><p>事实上，大多数付费插件都能找到很好甚至更好的免费替代,比如 BetterHUD 比付费的 HappyHUD(MythicHUD) 好很多</p><h2 id="插件能做的事" tabindex="-1">插件能做的事 <a class="header-anchor" href="#插件能做的事" aria-label="Permalink to &quot;插件能做的事&quot;">​</a></h2><p>来自 <a href="https://github.com/Godlander/objmc" target="_blank" rel="noreferrer">https://github.com/Godlander/objmc</a> :</p><p><img src="'+p+'" alt=""><img src="'+c+'" alt=""></p><p>来自 <a href="https://github.com/toxicity188/BetterHealthBar3" target="_blank" rel="noreferrer">https://github.com/toxicity188/BetterHealthBar3</a> :</p><p><img src="'+n+'" alt=""></p><p>来自 <a href="https://github.com/JNNGL/VanillaMinimaps" target="_blank" rel="noreferrer">https://github.com/JNNGL/VanillaMinimaps</a> :</p><p><img src="'+h+'" alt=""><img src="'+m+'" alt=""></p><p>来自 <a href="https://www.spigotmc.org/resources/itemframe-touchscreens-advancedgui.83636/" target="_blank" rel="noreferrer">https://www.spigotmc.org/resources/itemframe-touchscreens-advancedgui.83636/</a></p><p><img src="'+d+'" alt=""></p><p>还有 <a href="https://github.com/TheCymaera/minecraft-spider" target="_blank" rel="noreferrer">https://github.com/TheCymaera/minecraft-spider</a></p><h2 id="插件不能做的事" tabindex="-1">插件不能做的事 <a class="header-anchor" href="#插件不能做的事" aria-label="Permalink to &quot;插件不能做的事&quot;">​</a></h2><p>例如（无客户端 mod 的情况）：</p><ul><li>插件不能做自定义新的按键(原版仅能监听 Shift,F,Q 共四种组合)</li><li>获取玩家视角（第一人称，第二人称，第三人称）</li><li>不靠资源包或 mod 什么的不能修改计分板的背景</li><li>不大可能检测和拦截灵魂出窍</li></ul><p>总之，一些纯客户端的行为，插件是不能控制的。</p><h2 id="圈内破事" tabindex="-1">圈内破事 <a class="header-anchor" href="#圈内破事" aria-label="Permalink to &quot;圈内破事&quot;">​</a></h2><h3 id="miao-系列插件" tabindex="-1">miao 系列插件 <a class="header-anchor" href="#miao-系列插件" aria-label="Permalink to &quot;miao 系列插件&quot;">​</a></h3><p>因为配置简单(没啥功能当然配置简单)而广受小白服主青睐</p><p>之前被爆插件有远程执行代码后门来着</p><p>详情请见：<a href="https://lezi.8aka.org/article/%E5%9C%9F%E7%9A%87%E5%B8%9D%E5%96%B5" target="_blank" rel="noreferrer">https://lezi.8aka.org/article/土皇帝喵</a></p><h3 id="米饭系列插件" tabindex="-1">米饭系列插件 <a class="header-anchor" href="#米饭系列插件" aria-label="Permalink to &quot;米饭系列插件&quot;">​</a></h3><p>跟 miao 系列插件走差不多的道路</p><blockquote><p>只要我的插件有 GUI 界面，不管设计的多傻逼，功能少的有多可怜，那也有人夸好用！</p></blockquote><p>作者剽窃国外作者开源的插件，声称是自己的作品并公开售卖</p><p>详情请见：<a href="https://lezi.8aka.org/article/%E7%89%88%E4%B8%BB%E7%B1%B3%E9%A5%AD%E4%BC%A0" target="_blank" rel="noreferrer">https://lezi.8aka.org/article/版主米饭传</a></p>',28)),o(e)])}const x=t(f,[["render",g]]);export{v as __pageData,x as default};
