import{_ as e,c as t,a5 as n,o as i}from"./chunks/framework.O9ad3XaM.js";const m=JSON.parse('{"title":"服务器维护","description":"","frontmatter":{"title":"服务器维护","slug":"/maintenance","sidebar_position":2},"headers":[],"relativePath":"nitwikit/docs/process/maintenance/maintenance.md","filePath":"nitwikit/docs/process/maintenance/maintenance.md","lastUpdated":1729683245000}'),r={name:"nitwikit/docs/process/maintenance/maintenance.md"};function s(o,a,c,p,l,d){return i(),t("div",null,a[0]||(a[0]=[n('<h1 id="服务器维护" tabindex="-1">服务器维护 <a class="header-anchor" href="#服务器维护" aria-label="Permalink to &quot;服务器维护&quot;">​</a></h1><p>当你搭建好服务器后，需要做一些维护工作来保证服务器良好的运行，除非你压根不考虑玩家数据安全问题。</p><h2 id="安装防熊插件" tabindex="-1">安装防熊插件 <a class="header-anchor" href="#安装防熊插件" aria-label="Permalink to &quot;安装防熊插件&quot;">​</a></h2><p>类似 CoreProtect 等防熊插件可以记录玩家的几乎所有操作。</p><p>如果遇到熊孩子恶意毁坏，可以使用插件回溯功能单独回溯熊孩子的操作。</p><p>即使在区块出现损坏的情况下，这些插件也能在一定程度上减少区块损坏所造成的影响。见 <a href="https://nitwikit.yizhan.wiki/Java/AntiGrief" target="_blank" rel="noreferrer">防熊插件</a>。</p><h2 id="检查日志" tabindex="-1">检查日志 <a class="header-anchor" href="#检查日志" aria-label="Permalink to &quot;检查日志&quot;">​</a></h2><p><a href="/docs/start/basic/basic.html">日志</a>中包含了大量的玩家、插件、服务器行为。</p><p>每次维护服务器时请务必查看服务器近期的日志。</p><p>对于 ERROR 等级的报错请立即处理，自行在网络查询或咨询其他人。</p><p>对于 WARN 等级的报错，请尝试通过翻译等手段了解 warn 的内容，自行在网络查询或咨询其他人。</p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>注意！咨询其他人时请先查看 <a href="/docs/start/ask-for-help.html">如何向大佬求助</a></p></div>',12)]))}const f=e(r,[["render",s]]);export{m as __pageData,f as default};
